INSERT INTO `wp_snippets` (`id`, `name`, `description`, `code`, `tags`, `scope`, `priority`, `active`, `modified`, `revision`, `cloud_id`) VALUES
(1, 'Hacer que los nombres de archivos subidos estén en minúsculas', 'Asegura que las subidas de imágenes y archivos tengan nombres de archivo en minúsculas.\n\nEsto es un fragmento de código de ejemplo. Siéntente libre de usarlo, editarlo o eliminarlo.', 'add_filter( \'sanitize_file_name\', \'mb_strtolower\' );', 'sample, media', 'global', 10, 0, '2025-04-22 21:40:45', 2, NULL),
(2, 'Desactivar barra de administración', 'Desactiva la barra de administración de WordPress para todos excepto para los administradores.\n\nEsto es un fragmento de código de ejemplo. Siéntente libre de usarlo, editarlo o eliminarlo.', 'add_action( \'wp\', function () {\n	if ( ! current_user_can( \'manage_options\' ) ) {\n		show_admin_bar( false );\n	}\n} );', 'sample, admin-bar', 'front-end', 10, 0, '2025-04-22 21:40:45', 2, NULL),
(3, 'Permitir emoticonos', 'Permite la conversión de emoticonos en lugares oscuros.\n\nEsto es un fragmento de código de ejemplo. Siéntente libre de usarlo, editarlo o eliminarlo.', 'add_filter( \'widget_text\', \'convert_smilies\' );\nadd_filter( \'the_title\', \'convert_smilies\' );\nadd_filter( \'wp_title\', \'convert_smilies\' );\nadd_filter( \'get_bloginfo\', \'convert_smilies\' );', 'sample', 'global', 10, 0, '2025-04-22 21:40:45', 2, NULL),
(4, 'Año actual', 'Shortcode para insertar el año actual en una entrada o página…\n\nEsto es un fragmento de código de ejemplo. Siéntente libre de usarlo, editarlo o eliminarlo.', '<?php echo date( \'Y\' ); ?>', 'sample, dates', 'content', 10, 0, '2025-04-22 21:40:45', 2, NULL),
(5, 'newsletter_subscribe.php', '', '// Incluye las clases de PHPMailer\r\nuse PHPMailer\\PHPMailer\\PHPMailer;\r\nuse PHPMailer\\PHPMailer\\Exception;\r\n\r\nfunction mi_suscripcion_newsletter() {\r\n    global $wpdb;\r\n\r\n    if (isset($_POST[\'submit_newsletter\'])) {\r\n        // Limpiar el email recibido\r\n        $email = sanitize_email($_POST[\'email-form-newsletter\']);\r\n\r\n        if (is_email($email)) {\r\n            // Comprobar si el email ya existe en la tabla wp_users\r\n            $existing_user = $wpdb->get_var($wpdb->prepare(\r\n                \"SELECT user_email FROM {$wpdb->users} WHERE user_email = %s\",\r\n                $email\r\n            ));\r\n\r\n            if ($existing_user) {\r\n                echo \"<p>Este correo ya está registrado en nuestro sistema.</p>\";\r\n            } else {\r\n                // Crear los datos del nuevo usuario\r\n                $user_login = sanitize_user(substr($email, 0, strpos($email, \'@\')));\r\n                $user_pass = wp_generate_password();\r\n                $display_name = sanitize_text_field($email);\r\n                $user_nicename = sanitize_title($user_login);\r\n\r\n                // Insertar el nuevo usuario en la base de datos\r\n                $inserted = $wpdb->insert(\r\n                    $wpdb->users,\r\n                    array(\r\n                        \'user_login\' => $user_login,\r\n                        \'user_pass\' => wp_hash_password($user_pass),\r\n                        \'user_nicename\' => $user_nicename,\r\n                        \'user_email\' => $email,\r\n                        \'user_registered\' => current_time(\'mysql\'),\r\n                        \'display_name\' => $display_name\r\n                    ),\r\n                    array(\'%s\', \'%s\', \'%s\', \'%s\', \'%s\', \'%s\')\r\n                );\r\n\r\n                if ($inserted) {\r\n                    echo \"<p>¡Te has suscrito exitosamente a nuestra newsletter!</p>\";\r\n\r\n                    // Configuración de PHPMailer para enviar el correo de confirmación\r\n                    try {\r\n                        $mail = new PHPMailer(true); // Usamos true para manejar excepciones\r\n\r\n                        // Configuración del servidor SMTP\r\n                        $mail->isSMTP();\r\n                        $mail->Host = \'smtp.gmail.com\'; // Servidor SMTP de Gmail\r\n                        $mail->SMTPAuth = true;\r\n                        $mail->Username = \'farinacaamanoalex@gmail.com\'; // Tu correo de Gmail\r\n                        $mail->Password = \'gncaulxusahqphdk\'; // Tu contraseña de Gmail\r\n                        $mail->SMTPSecure = PHPMailer::ENCRYPTION_SMTPS; // Usamos SMTPS (SSL/TLS)\r\n                        $mail->Port = 465; // Puerto de Gmail para conexiones seguras\r\n\r\n                        // Remitente y destinatario\r\n                        $mail->setFrom(\'tu_email@gmail.com\', \'Tu nombre\'); // Cambia esto a tu dirección\r\n                        $mail->addAddress($email); // El email al que se enviará el mensaje\r\n\r\n                        // Contenido del correo\r\n                        $mail->isHTML(true); // Asegúrate de que el correo se enviará como HTML\r\n                        $mail->Subject = \'¡Gracias por tu suscripción!\';\r\n                        $mail->Body = \'Gracias por suscribirte a nuestra newsletter. <b>A partir de ahora, te informaremos de nuestras nuevas ofertas y novedades.</b>\';\r\n\r\n                        // Enviar el correo\r\n                        $mail->send();\r\n                        echo \'¡El mensaje se envió correctamente!\';\r\n                    } catch (Exception $e) {\r\n                        echo \"No se pudo enviar el mensaje. Error: {$mail->ErrorInfo}\";\r\n                    }\r\n                } else {\r\n                    echo \"<p>Hubo un error al suscribirte. Intentando insertar: </p>\";\r\n                    var_dump($wpdb->last_query); // Mostrar la última consulta SQL ejecutada\r\n                }\r\n            }\r\n        } else {\r\n            echo \"<p>Por favor, ingresa un correo electrónico válido.</p>\";\r\n        }\r\n    }\r\n}\r\n\r\nadd_action(\'wp_footer\', \'mi_suscripcion_newsletter\');\r\n', '', 'global', 10, 1, '2025-04-23 00:00:46', 10, NULL);

